mapper.insert:
insert into reservation(
	reservation_no, reservation_date, reservation_time, 
	member_id , grade_type , route_no , bus_no , seat_no
)
values(
 #{reservationNo},#{reservationDate},#{reservationTime},
 #{memberId}, #{gradeType} , #{routeNo} , #{busNo}, #{seatNo}
)

mapper.update:
update reservation set
reservation_date =#{reservationDate},
reservation_time =#{reservationTime},
member_id =#{memberId},
grade_type =#{gradeType},
route_no =#{routeNo}
bus_no =#{busNo},
seat_no =#{seatNo}
where reservation_no =#{reservationNo}

update reservation
reservation_no = #{reservationNo}, reservation_date = #{reservationDate}, reservation_time = #{reservationTime}, member_id = #{memberId}, gradeType = #{gradeType}, routeNo = #{routeNo}, busNo = #{busNo}, seatNo = #{seatNo}
where reservationNo = #{reservationNo}
