mapper.insert:
insert into review_like(
	member_id, review_no
)
values(
	#{memberId}, #{reviewNo}
)

mapper.delete:
delete review_like 
where 
member_id = #{memberId} and review_no = #{reviewNo}

mapper.select:
select count(*) from review_like
where
member_id = #{memberId} and review_no = #{reviewNo}
